select count(a.id) from (select od.id from shengxian_order o inner join shengxian_order_details od on od.order_id = o.id inner join (select id,name ,is_del from shengxian_goods) g on g.id = od.goods_id and g.is_del = 0 inner join (select goods_id,warehouse_id,fictitious from shengxian_goods_inventory) gi on gi.goods_id = g.id where o.is_del=0 and o.mold=0 and o.status in(2,3) and o.business_id = #{business_id} and o.print_frequ = 1
and gi.warehouse_id = #{wid}
group by g.id )a

select od.id,g.number, g.`name`,sum(od.order_number)order_number,gi.fictitious,g.units, sum((od.order_number * od.order_price))price,od.order_price, (select name from shengxian_warehouse where id=gi.warehouse_id)wname from shengxian_order o inner join shengxian_order_details od on od.order_id = o.id inner join (select id,name,units ,number , is_del from shengxian_goods) g on g.id = od.goods_id and g.is_del = 0 inner join (select goods_id,warehouse_id,fictitious from shengxian_goods_inventory) gi on gi.goods_id = g.id where o.is_del = 0 and o.mold = 0 and o.status in(2,3) and o.business_id=#{business_id} and o.print_frequ = 1
and gi.warehouse_id = #{wid}
group by g.id order by o.create_time desc limit #{startIndex},#{pageSize}

select ifnull(sum(a.price),0)money from (select sum((od.order_number * od.order_price))price from shengxian_order o inner join shengxian_order_details od on od.order_id = o.id inner join (select id,name ,is_del from shengxian_goods) g on g.id = od.goods_id and g.is_del = 0 inner join (select goods_id,warehouse_id,fictitious from shengxian_goods_inventory) gi on gi.goods_id = g.id where o.is_del = 0 and o.mold = 0 and o.status in(2,3) and o.business_id=#{business_id} and o.print_frequ = 1
and gi.warehouse_id=#{wid}
group by g.id )a

select od.id,(select user_name from shengxian_binding where id=o.binding_id)user_name,g.units,g.number, g.`name`, od.order_number,(od.order_number*od.order_price)price,gi.fictitious,od.order_price, (select number from shengxian_binding where id=o.binding_id)num, (select name from shengxian_warehouse where id=gi.warehouse_id)wname from shengxian_order o inner join shengxian_order_details od on od.order_id = o.id inner join (select id,name ,units ,number,is_del from shengxian_goods) g on g.id=od.goods_id and g.is_del = 0 inner join (select goods_id,warehouse_id,fictitious from shengxian_goods_inventory) gi on gi.goods_id = g.id where o.is_del = 0 and o.mold = 0 and o.status in(2,3) and o.business_id = #{business_id} and o.print_frequ = 1
and gi.warehouse_id = #{wid}
order by o.create_time desc limit #{startIndex},#{pageSize}

