select dd.id,dd.order_id,o.order_number ,o.price,o.create_time,o.status, o.state,o.audit_time,o.receivable_time,b.user_name as name,o.payee ,s.name as sname , o.money from shengxian_dispatch_detail dd inner join shengxian_order o on o.id = dd.order_id inner join shengxian_dispatch d on d.id = dd.dispatch_id inner join (select id,user_name,is_del from shengxian_binding) b on b.id = o.binding_id and b.is_del = 0 inner join shengxian_staff s on s.id = d.staff_id where o.is_del = 0 and dd.dispatch_id = #{id} and o.status not in (5,6)
and dd.is_del = #{is_del}
and d.status = #{status}
and o.order_number = #{number}

