mapper.select:
select o.id,o.no, o.order_number,o.audit_time ,o.making,o.deliver,o.staff_id ,0 as type , bd.user_name as name ,o.money ,o.price from shengxian_order o inner join shengxian_binding bd on bd.id = o.binding_id where o.is_del=0 and o.staff_id = #{staff_id} and DATE_FORMAT(o.audit_time,'%Y-%m') = #{time}
and bd.user_name like CONCAT('%' ,#{name} ,'%')
order by o.audit_time desc limit #{startIndex} ,#{pageSize}

select p.id,p.no,p.order_number,p.audit_time,p.making,p.auditor as deliver,p.staff_id ,1 as type, s.name ,p.money ,p.price from shengxian_purchase p inner join shengxian_suppliers s on s.id = p.suppliers_id where p.is_del=0 and p.staff_id = #{staff_id} and DATE_FORMAT(p.audit_time,'%Y-%m') = #{time}
and s.name like CONCAT('%' ,#{name} ,'%')
order by p.audit_time desc limit #{startIndex} ,#{pageSize}
