mapper.select:
select count(1)
from shengxian_goods g
inner join shengxian_goods_category gc on gc.id = g.category_id
inner join shengxian_goods_inventory gi on gi.goods_id = g.id
inner join shengxian_warehouse w on w.id = gi.warehouse_id
where g.is_del = 0 and  g.business_id = #{business_id}

    and gi.status = #{status}


    and g.id = #{goods_id}


    and g.name LIKE CONCAT('%',#{name},'%')


    and g.number LIKE CONCAT('%',#{number},'%')


    and ( gc.id = #{category_id} or gc.level = #{category_id} )


    and w.id = #{warehouse_id}

select g.id,gc.`name`as cname,g.number,g.name,w.id as wid,w.name as wname, g.cost_price,g.units,gi.id as inventory_id,gi.actual,
gi.fictitious,gi.update_inventory,(g.cost_price*gi.actual) as price1 ,g.state ,(g.cost_price * gi.update_inventory) as price
from shengxian_goods g
inner join shengxian_goods_category gc on gc.id = g.category_id
inner join shengxian_goods_inventory gi on gi.goods_id = g.id
inner join shengxian_warehouse w on w.id = gi.warehouse_id
where g.is_del = 0 and  g.business_id = #{business_id}

    and gi.status = #{status}


    and g.id = #{goods_id}


    and g.name LIKE CONCAT('%',#{name},'%')


    and g.number LIKE CONCAT('%',#{number},'%')


    and ( gc.id = #{category_id} or gc.level = #{category_id} )


    and w.id = #{warehouse_id}

order by g.state asc limit #{startIndex},#{pageSize}

mapper.update:
update shengxian_goods
state = #{state}, time = #{time},
where id = #{id}

mapper.select:
select count(g.id) from shengxian_goods g inner join shengxian_goods_category gc on gc.id = g.category_id inner join shengxian_goods_inventory gi on gi.goods_id = g.id inner join shengxian_warehouse w on w.id = gi.warehouse_id where g.is_del = 0 and g.business_id = #{business_id} and gi.status in (1,2)
and g.id = #{goods_id}
and g.name LIKE CONCAT('%',#{name},'%')
and g.number LIKE CONCAT('%',#{number},'%')
and ( gc.id = #{category_id} or gc.level = #{category_id} )
and w.id = #{warehouse_id}

select g.id,gc.`name`as cname,g.number,g.name,w.name as wname, g.cost_price,g.units,gi.id as inventory_id, gi.actual,gi.fictitious,gi.update_inventory,(g.cost_price*gi.update_inventory) as price from shengxian_goods g inner join shengxian_goods_category gc on gc.id = g.category_id inner join shengxian_goods_inventory gi on gi.goods_id = g.id inner join shengxian_warehouse w on w.id = gi.warehouse_id where g.is_del = 0 and g.business_id = #{business_id} and gi.status in (1,2)
and g.id = #{goods_id}
and g.name LIKE CONCAT('%',#{name},'%')
and g.number LIKE CONCAT('%',#{number},'%')
and ( gc.id = #{category_id} or gc.level = #{category_id} )
and w.id = #{warehouse_id}
order by g.number asc limit #{startIndex},#{pageSize}

select IFNULL( sum(gi.update_inventory * g.cost_price),0) from shengxian_goods g inner join shengxian_goods_category gc on gc.id = g.category_id inner join shengxian_goods_inventory gi on gi.goods_id = g.id inner join shengxian_warehouse w on w.id = gi.warehouse_id where g.is_del = 0 and g.business_id = #{business_id} and gi.status in (1,2)
and g.id = #{goods_id}
and g.name LIKE CONCAT('%',#{name},'%')
and g.number LIKE CONCAT('%',#{number},'%')
and ( gc.id = #{category_id} or gc.level = #{category_id} )
and w.id = #{warehouse_id}

select  gi.id from shengxian_goods g
inner join shengxian_goods_inventory gi on gi.goods_id = g.id
where g.is_del=0  and gi.status in (1,2) and g.business_id = #{business_id}

select count(id) from shengxian_goods_settlement
where business_id=#{business_id}

    and  create_time <=#{endTime} and create_time >=#{startTime}

select id ,create_time,if(opid=0,'商家',(select name from shengxian_staff where opid=id))name
from shengxian_goods_settlement
where business_id=#{business_id}

    and  create_time <=#{endTime} and create_time >=#{startTime}

order by create_time desc limit #{startIndex},#{pageSize}

select count(gs.id) from shengxian_goods g inner join shengxian_goods_settlement_detail gs on gs.goods_id = g.id inner join shengxian_goods_category gc on gc.id = g.category_id inner join (select goods_id,warehouse_id from shengxian_goods_inventory) gi on gi.goods_id=g.id inner join shengxian_warehouse w on w.id = gi.warehouse_id where gs.settlement_id = #{settlement_id} and gs.is_del = 0
and g.name LIKE CONCAT('%',#{name},'%')
and g.number LIKE CONCAT('%',#{number},'%')
and (gc.id LIKE CONCAT('%',#{category_id},'%') or gc.level LIKE CONCAT('%',#{category_id},'%'))
and gs.opid = #{opid}
and w.id = #{warehouse_id}

select gs.id,gc.`name`as cname,g.number,g.name,w.name as wname, g.cost_price,g.units,gs.actual,gs.num,gs.create_time, (g.cost_price * gs.num)price ,if(gs.opid=0 ,'商家',(select name from shengxian_staff where gs.opid=id))sname from shengxian_goods g inner join shengxian_goods_settlement_detail gs on gs.goods_id = g.id inner join shengxian_goods_category gc on gc.id = g.category_id inner join (select goods_id,warehouse_id from shengxian_goods_inventory) gi on gi.goods_id = g.id inner join shengxian_warehouse w on w.id = gi.warehouse_id where gs.settlement_id = #{settlement_id} and gs.is_del = 0
and g.name LIKE CONCAT('%',#{name},'%')
and g.number LIKE CONCAT('%',#{number},'%')
and (gc.id LIKE CONCAT('%',#{category_id},'%') or gc.level LIKE CONCAT('%',#{category_id},'%'))
and gs.opid = #{opid}
and w.id = #{warehouse_id}
order by gs.create_time asc limit #{startIndex},#{pageSize}

select IFNULL(sum(gs.num * g.cost_price),0)price from shengxian_goods g inner join shengxian_goods_settlement_detail gs on gs.goods_id = g.id inner join shengxian_goods_category gc on gc.id = g.category_id inner join (select goods_id,warehouse_id from shengxian_goods_inventory) gi on gi.goods_id = g.id inner join shengxian_warehouse w on w.id = gi.warehouse_id where gs.settlement_id = #{settlement_id} and gs.is_del = 0
and g.name LIKE CONCAT('%',#{name},'%')
and g.number LIKE CONCAT('%',#{number},'%')
and (gc.id LIKE CONCAT('%',#{category_id},'%') or gc.level LIKE CONCAT('%',#{category_id},'%'))
and w.id = #{warehouse_id}
and gs.opid = #{opid}

select g.id,gc.`name`as cname,g.number,g.name,w.id as wid,w.name as wname, g.cost_price,g.units,gi.id as inventory_id,gi.actual,
gi.fictitious,gi.update_inventory,(g.cost_price*gi.actual) as price1 ,g.state ,(g.cost_price * gi.update_inventory) as price
from shengxian_goods g
inner join shengxian_goods_category gc on gc.id = g.category_id
inner join shengxian_goods_inventory gi on gi.goods_id = g.id
inner join shengxian_warehouse w on w.id = gi.warehouse_id
where g.is_del = 0 and  g.business_id = #{business_id}

    and w.id = #{wid}

order by g.state asc ,g.id asc limit #{startIndex},#{pageSize}

select IFNULL(SUM(g.cost_price * gi.actual),0)price from shengxian_goods g inner join shengxian_goods_category gc on gc.id = g.category_id inner join shengxian_goods_inventory gi on gi.goods_id = g.id inner join shengxian_warehouse w on w.id = gi.warehouse_id where g.is_del = 0
and g.business_id = #{business_id}
and w.id = #{wid}

select count(g.id) from shengxian_goods g inner join shengxian_give_goods gg on gg.goods_id = g.id inner join shengxian_goods_inventory gi on gi.goods_id = g.id inner join shengxian_suppliers s on s.id = gg.suppliers_id where g.business_id = #{business_id} and date_sub(now(),INTERVAL 365 DAY) <= date(gg.give_time)
AND DATE_FORMAT(gg.give_time,'%Y-%m-%d') <= #{endTime} AND DATE_FORMAT(gg.give_time,'%Y-%m-%d') >= #{startTime}
and gi.warehouse_id = #{warehouse_id}
and (g.name LIKE CONCAT('%',#{name},'%') or s.name LIKE CONCAT('%',#{name},'%'))

select gg.id,gg.give_time,g.id as goods_id,g.`name`,(select name from shengxian_warehouse where id=gi.warehouse_id)wname,gg.num,g.units, g.cost_price ,s.`name`as suppliers_name ,gg.status,gg.beizhu, IFNULL((select ss.name from shengxian_staff ss where ss.id=gg.operate_id ),"商家")operate_name from shengxian_goods g inner join shengxian_give_goods gg on gg.goods_id = g.id inner join shengxian_goods_inventory gi on gi.goods_id = g.id inner join shengxian_suppliers s on s.id = gg.suppliers_id where g.business_id = #{business_id} and date_sub(now(),INTERVAL 365 DAY) <= date(gg.give_time)
AND DATE_FORMAT(gg.give_time,'%Y-%m-%d') <= #{endTime} AND DATE_FORMAT(gg.give_time,'%Y-%m-%d') >= #{startTime}
and gi.warehouse_id = #{warehouse_id}
and (g.name LIKE CONCAT('%',#{name},'%') or s.name LIKE CONCAT('%',#{name},'%'))
order by gg.give_time desc limit #{startIndex},#{pageSize}

select count(g.id) from shengxian_goods g inner join shengxian_loss_goods lg on lg.goods_id = g.id inner join shengxian_goods_inventory gi on gi.goods_id = g.id inner join shengxian_binding b on b.id = lg.binding_id where g.business_id = #{business_id} and date_sub(now(),INTERVAL 365 DAY) <= date(lg.loss_time)
AND DATE_FORMAT(lg.loss_time,'%Y-%m-%d') <= #{endTime} AND DATE_FORMAT(lg.loss_time,'%Y-%m-%d') >= #{startTime}
and gi.warehouse_id = #{warehouse_id}
and (g.name LIKE CONCAT('%',#{name},'%') or b.user_name LIKE CONCAT('%',#{name},'%'))

select lg.loss_time,g.id,g.`name`,(select name from shengxian_warehouse where id = gi.warehouse_id)wname,lg.num,g.units, g.cost_price ,b.user_name as bind_name,lg.status,lg.beizhu, IFNULL((select ss.name from shengxian_staff ss where ss.id=lg.operate_id ),"商家")operate_name from shengxian_goods g inner join shengxian_loss_goods lg on lg.goods_id = g.id inner join shengxian_goods_inventory gi on gi.goods_id = g.id inner join shengxian_binding b on b.id = lg.binding_id where g.business_id = #{business_id} and date_sub(now(),INTERVAL 365 DAY) <= date(lg.loss_time)
AND DATE_FORMAT(lg.loss_time,'%Y-%m-%d') <= #{endTime} AND DATE_FORMAT(lg.loss_time,'%Y-%m-%d') >= #{startTime}
and gi.warehouse_id = #{warehouse_id}
and (g.name LIKE CONCAT('%',#{name},'%') or b.user_name LIKE CONCAT('%',#{name},'%'))
order by lg.loss_time desc limit #{startIndex},#{pageSize}

select g.id,gc.`name`as categoryName,g.number,g.name,w.name as wname, g.cost_price ,g.units,gi.actual ,gi.fictitious ,g.cost_price * gi.actual as price from shengxian_goods g inner join shengxian_goods_category gc on gc.id = g.category_id inner join shengxian_goods_inventory gi on gi.goods_id = g.id inner join shengxian_warehouse w on w.id = gi.warehouse_id where g.is_del = 0 and g.business_id = #{business_id} and w.business_id = #{business_id}
and w.id = #{id}
order by g.state asc ,g.id asc

select IFNULL(SUM(g.cost_price * gi.actual),0)price
from shengxian_goods g
inner join shengxian_goods_category gc on gc.id = g.category_id
inner join shengxian_goods_inventory gi on gi.goods_id = g.id
inner join shengxian_warehouse w on w.id = gi.warehouse_id
where  g.is_del = 0

    and g.business_id = #{business_id}


    and gi.status = #{status}


    and g.id = #{goods_id}


    and g.name LIKE CONCAT('%',#{name},'%')


    and g.number LIKE CONCAT('%',#{number},'%')


    and ( gc.id = #{category_id} or gc.level = #{category_id} )


    and w.id = #{warehouse_id}

select gg.id,gg.give_time,g.id as goods_id,g.`name`,(select name from shengxian_warehouse where id=gi.warehouse_id)wname,gg.num,g.units, g.cost_price ,s.`name`as suppliers_name ,gg.status, IFNULL((select ss.name from shengxian_staff ss where ss.id=gg.operate_id ),"商家")operate_name from shengxian_goods g inner join shengxian_give_goods gg on gg.goods_id = g.id inner join shengxian_goods_inventory gi on gi.goods_id = g.id inner join shengxian_suppliers s on s.id = gg.suppliers_id where g.business_id = #{business_id} and date_sub(now(),INTERVAL 365 DAY) <= date(gg.give_time)
AND DATE_FORMAT(gg.give_time,'%Y-%m-%d') <= #{endTime} AND DATE_FORMAT(gg.give_time,'%Y-%m-%d') >= #{startTime}
and gi.warehouse_id = #{warehouse_id}
and (g.name LIKE CONCAT('%',#{name},'%') or s.name LIKE CONCAT('%',#{name},'%'))
order by gg.give_time desc

select lg.loss_time,g.id,g.`name`,(select name from shengxian_warehouse where id=gi.warehouse_id)wname,lg.num,g.units, g.cost_price ,b.user_name as bind_name,lg.status, IFNULL((select ss.name from shengxian_staff ss where ss.id=lg.operate_id ),"商家")operate_name from shengxian_goods g inner join shengxian_loss_goods lg on lg.goods_id = g.id inner join shengxian_goods_inventory gi on gi.goods_id = g.id inner join shengxian_binding b on b.id = lg.binding_id where g.business_id = #{business_id} and date_sub(now(),INTERVAL 365 DAY) <= date(lg.loss_time)
AND DATE_FORMAT(lg.loss_time,'%Y-%m-%d') <= #{endTime} AND DATE_FORMAT(lg.loss_time,'%Y-%m-%d') >= #{startTime}
and gi.warehouse_id=#{warehouse_id}
and (g.name LIKE CONCAT('%',#{name},'%') or b.user_name LIKE CONCAT('%',#{name},'%'))
order by lg.loss_time desc
